public String eraseExtraComma(String psCheckValue){

    StringBuffer sb = new StringBuffer(psCheckValue)

    int i,j

    for (i = 0; i < sb.length(); i++) {
        if (sb.charAt(i)=='\"') {
            //print(i)
            for (j = i+1; j < sb.length(); j++) {
                if (sb.charAt(j)==',') {
                    //print(j)
                    sb.deleteCharAt(j)
                    break
                }
            }
            break
        }
    }
    return sb;

}

