String eraseExtraComma(String psCheckValue) {
    StringBuffer sb = new StringBuffer(psCheckValue)
    int i, j, k, l,a,b,c,d
    for(i=0;i<sb.length();i++){
        if(sb.charAt(i)=='\"'){
            k=i
            for(j=i+1;j<sb.length();j++){
                if(sb.charAt(j)=='\"'){
                    l=j-1
                    break
                }
            }
            break
        }

    }
    for(int p=k+1;p<l-1;p++){
        if(sb.charAt(p)==','){
            sb.deleteCharAt(p)
        }
    }
    StringBuffer sb1=sb
    for(a=sb1.length()-1;a>0;a--){
        if(sb1.charAt(a)=='\"'){
            c=a
            for(b=a-1;b>0;b--){
                if(sb1.charAt(b)=='\"'){
                    d=b
                    break
                }
            }
            break
        }
    }
    for(int q =c-1;q>d+1;q--){
        if(sb1.charAt(q)==','){
            sb1.deleteCharAt(q)
        }
    }

    if(sb1.contains('\'')){
        for(i=0;i<sb.length();i++){
            if(sb1.charAt(i)=='\'')
                sb1.deleteCharAt(i)
        }
    }
    return sb1
}

