Logger log = Logger.getLogger("HostsWithGroupsLog");
String strInputData = new File("C:\\Data_Items\\HostswithGroups.json").text
json_sup = new JsonSlurper()
json_response = json_sup.parseText(strInputData)

LocalDateTime date = LocalDateTime.now()
String date_time = date.format("yyyy-MM-dd' 'HH:mm:ss")
log.info("date="+date_time)

String str, store_id, terminal_type, group_nm


def results = []
int totalRowCount=0;

try {
        for (int i = 0; i < json_response.hosts.size(); i++) {
            //Logic for store_id & terminal_type
			str = json_response.hosts.description[i]

			if (str.contains("WSKDS")) {
				store_id = str[-6..-1]
				terminal_type = "KDS"
			}
			else if (str.contains("POS") && str.length()>11){
				store_id = str.substring(2,8)
				terminal_type = "POS"
			}
			else {
				store_id = str
				terminal_type = "UAT"
			}
			for (int j = 0; j < json_response.groups.size(); j++){
				if (json_response.groups.id[j] == json_response.hosts.groupid[i])
					group_nm = json_response.groups.name[j]
			}

			//Preparing SQL Query
			def query = "INSERT INTO public.\"HostswithGroups\"(store_id, group_id, group_name, terminal_id, terminal_desc, \"hostStateChangeDate\", \"isHostOnline\", terminal_type, createts, modifyts) " +
					"VALUES ('"+store_id+
					"','"+json_response.hosts.groupid[i]+
					"','"+group_nm+"','"+json_response.hosts.id[i]+
					"','"+json_response.hosts.description[i]+
					"','"+json_response.hosts.hostStateChangeDate[i]+
					"','"+json_response.hosts.isHostOnline[i]+
					"','"+terminal_type+
					"','"+date_time+
					"','"+date_time+"');"

            //log.info("\n query " + query);
            try {
                SQL.mydb.withTransaction {
                    SQL.mydb.execute(query);
                    results = "{True}"
                }
            } catch (Exception e) { 
                log.info("\n Error Loading data for " + query);
                log.info("........................................");
            }
        }// End of For Loop i

//write results as a new flow file content
    ff.write("UTF-8") { writer ->
        new JsonBuilder(results).writeTo(writer)
    }
//transfer to success
    REL_SUCCESS << ff
}
 catch(ex) {
    results="{False}"
    //log.info('!! Error while Data Audit:', ex)
//write results as a new flow file content
    ff.write("UTF-8"){writer->
        new JsonBuilder(results).writeTo(writer)
    }
//transfer to failure
    REL_FAILURE << ff
}
